-- ----------------------------
-- View structure for qty_total_sewing_line
-- ----------------------------
DROP VIEW IF EXISTS `qty_total_sewing_line`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `qty_total_sewing_line` AS SELECT
output_sewing_detail.id_output_sewing,
output_sewing_detail.orc,
output_sewing.tgl,
output_sewing.line,
Sum(output_sewing_detail.qty) AS qty,
COALESCE(output_sewing_detail.center_panel_sam_result,0) AS sam_cp,
COALESCE(output_sewing_detail.back_wings_sam_result,0) AS sam_bw,
output_sewing_detail.cups_sam_result AS sam_cup,
output_sewing_detail.assembly_sam_result AS sam_ass,
output_sewing_detail.center_panel_sam_result + output_sewing_detail.back_wings_sam_result + output_sewing_detail.cups_sam_result + output_sewing_detail.assembly_sam_result AS total_sam,
output_sewing.cups_op + output_sewing.back_wings_op + output_sewing.center_panel_op + output_sewing.assembly_op AS total_op,
ROUND(Sum(output_sewing_detail.qty) * ((output_sewing_detail.center_panel_sam_result + output_sewing_detail.back_wings_sam_result + output_sewing_detail.cups_sam_result + output_sewing_detail.assembly_sam_result) /60) / (14 * (output_sewing.cups_op + output_sewing.back_wings_op + output_sewing.center_panel_op + output_sewing.assembly_op)) * 100,2) AS eff
FROM
output_sewing
INNER JOIN output_sewing_detail ON output_sewing_detail.id_output_sewing = output_sewing.id_output_sewing
WHERE 
output_sewing.tgl 
GROUP BY
output_sewing.tgl,
output_sewing.line ;

