-- ----------------------------
-- View structure for view_total_sewing_line
-- ----------------------------
DROP VIEW IF EXISTS `view_total_sewing_line`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `view_total_sewing_line` AS SELECT
output_sewing_detail.id_output_sewing,
output_sewing_detail.orc,
WEEKDAY( output_sewing.tgl ) AS weekday,
output_sewing.tgl,
output_sewing.line,
Sum(output_sewing_detail.qty) AS qty,
output_sewing_detail.center_panel_sam_result + output_sewing_detail.back_wings_sam_result + output_sewing_detail.cups_sam_result + output_sewing_detail.assembly_sam_result AS total_sam,
output_sewing.cups_op + output_sewing.back_wings_op + output_sewing.center_panel_op + output_sewing.assembly_op AS total_op,
IF
	(
		WEEKDAY( output_sewing.tgl ) > '5',
		ROUND(
			(
				 Sum( output_sewing_detail.qty )  * (14.890) / 60 
			) / ( 5 * output_sewing.cups_op + output_sewing.back_wings_op + output_sewing.center_panel_op + output_sewing.assembly_op ) * 100,
			2 
		),
		ROUND(
			(
				 Sum( output_sewing_detail.qty )  * (14.890) 
			) / ( 7 * ( output_sewing.cups_op + output_sewing.back_wings_op + output_sewing.center_panel_op + output_sewing.assembly_op ) * 60 ) * 100,
			2 
		) 
	) AS eff
FROM
output_sewing
INNER JOIN output_sewing_detail ON output_sewing_detail.id_output_sewing = output_sewing.id_output_sewing
GROUP BY
output_sewing.tgl,
output_sewing.line ;

