-- ----------------------------
-- View structure for v_barcutting_daily
-- ----------------------------
DROP VIEW IF EXISTS `v_barcutting_daily`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `v_barcutting_daily` AS SELECT
	cutting_detail.id_cutting_detail,
	WEEKDAY( input_cutting.tgl ) AS weekday,
	input_cutting.tgl,
	input_cutting.orc,
	input_cutting.style,
	cutting_detail.cutting_sam,
	Sum( input_cutting.qty_pcs ) AS cut,
IF
	(
		WEEKDAY( input_cutting.tgl ) > '5',
		ROUND(
			( ( Sum( input_cutting.qty_pcs ) ) * cutting_detail.cutting_sam ) / ( 10 * 47 * 60 ) * 100,
			2 
		),
		ROUND(
			( ( Sum( input_cutting.qty_pcs ) ) * cutting_detail.cutting_sam ) / ( 21 * 47 * 60 ) * 100,
			2 
		) 
	) AS eff 
FROM
	cutting_detail
	INNER JOIN input_cutting ON input_cutting.kode_barcode = cutting_detail.kode_barcode 
GROUP BY
	input_cutting.tgl ;

