CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `v_orc_packing` AS SELECT
output_packing.tgl,
output_packing.orc,
output_packing.karton,
Sum(output_packing.pcs) AS qty_packing,
cutting.qty AS `order`,
cutting.qty - Sum(output_packing.pcs) AS balance
