cutting.qty AS qty_order,
Sum(input_sewing.qty_pcs) AS qty_sewing,
cutting.qty - Sum(input_sewing.qty_pcs) AS balance
FROM
input_sewing
INNER JOIN cutting ON cutting.orc = input_sewing.orc
GROUP BY
input_sewing.orc ;

