DROP VIEW IF EXISTS `v_total_qty_sewing`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `v_total_qty_sewing` AS SELECT
output_sewing_detail.id_output_sewing,
output_sewing_detail.orc,
output_sewing.tgl,
output_sewing.line,
Sum(output_sewing_detail.qty) AS qty,
COALESCE(Sum(output_sewing_detail.center_panel_sam_result),0) AS sam_cp,
COALESCE(Sum(output_sewing_detail.back_wings_sam_result),0) AS sam_bw,
